import "bootstrap/dist/css/bootstrap.min.css";
import React from "react";
import "react-animated-slider/build/horizontal.css";
import { Col, Container, Row } from "react-bootstrap";
import Tooltip from "react-bootstrap/Tooltip";
import "../index.css";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";

const LANGS = ["Java", "JavaScript", "C & C++", "Python", "SQL", "HTML & CSS"];
const FRAMEWORKS = [
  "Hibernate",
  "JavaFX",
  "Spring",
  "JUnit",
  "React",
  "Bootstrap",
  "Node",
];
const TOOLS = [
  "Git & GitHub",
  "UML Diagrams",
  "Maven",
  "npm",
  "Eclipse & IntelliJ IDEA",
  "Excel & Google Sheets",
  "Slack & Trello & Notion",
];

export default class AboutSection extends React.Component {
  render() {
    return (
      <div>
        <Container style={{ margin: "15px" }}>
          <Row>
            <Col>
              <h5 className="headerText">Languages</h5>
              <ul>
                {LANGS.map((lang, index) => (
                  <li>{lang}</li>
                ))}
              </ul>
            </Col>
            <Col>
              <h5 className="headerText">Frameworks</h5>
              <ul>
                {FRAMEWORKS.map((t, index) => (
                  <li>{t}</li>
                ))}
              </ul>
            </Col>
            <Col>
              <h5 className="headerText">Tools</h5>
              <ul>
                {TOOLS.map((tool, index) => (
                  <li>{tool}</li>
                ))}
              </ul>
            </Col>
            <Col>
              <h5 className="headerText">Education</h5>
              <h5>BS, Computer Science</h5>
              <OverlayTrigger
                placement="bottom"
                delay={{ show: 25, hide: 25 }}
                overlay={renderTooltip}
              >
                <h5>Stony Brook University</h5>
              </OverlayTrigger>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

function renderTooltip(props) {
  return (
    <Tooltip id="code_tooltip" {...props}>
      What's a Seawolf?
    </Tooltip>
  );
}
