import "bootstrap/dist/css/bootstrap.min.css";
import React from "react";
import "react-animated-slider/build/horizontal.css";
import { Col, Container, Row } from "react-bootstrap";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import ReactDOM from "react-dom";
import Sidebar from "react-sidebar";
import SbuImg from "./images/sbu.png";
import "./index.css";

import ProjectPage from "./components/ProjectPage";
import AboutSection from "./components/AboutSection";
import SideBarContent from "./components/SideBarContent";

export const ABOUT = {
  INTRO:
    "Welcome! I'm primarly interested in backend development, but always trying to learn new tech. I love coding, math, and lifting heavy weights.",
  BUILT: "This website was made using React.", //arrow to github
  HEADLINE: "Software Developer",
  EMAIL: "pwamsley2019@gmail.com",
  GITHUB: "pwamsley2015.github.com",
  INSTA: "https://www.instagram.com/patrick_wamsley/",
};

const SIDEBAR_WIDTH = 600;

const mql = window.matchMedia("(min-width: " + SIDEBAR_WIDTH * 2 + "px)");
class HomeScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sidebarDocked: mql.matches,
      sidebarOpen: false,
      sidebarWidth: SIDEBAR_WIDTH,
    };

    this.mediaQueryChanged = this.mediaQueryChanged.bind(this);
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
  }

  componentWillMount() {
    mql.addListener(this.mediaQueryChanged);
  }

  componentWillUnmount() {
    this.state.mql.removeListener(this.mediaQueryChanged);
  }

  onSetSidebarOpen(open) {
    this.setState({
      sidebarOpen: open,
      sidebarWidth: SIDEBAR_WIDTH,
    });
  }

  mediaQueryChanged() {
    this.setState({ sidebarDocked: mql.matches, sidebarOpen: false });
  }

  render() {
    return (
      <div
        className="home_screen"
        style={{
          display: "flex",
          background: "#303C55",
          color: "#e6f1ff",
        }}
      >
        <Navbar bg="dark" variant="dark" fixed="top">
          <Navbar.Brand>
            <h1>Patrick Wamsley</h1>
          </Navbar.Brand>
          <Nav className="mr-auto">
            <Nav.Link href="#projects">Projects</Nav.Link>
            <Nav.Link href="resume.pdf">Resume PDF</Nav.Link>
          </Nav>
        </Navbar>

        <Sidebar
          sidebar={<SideBarContent />}
          docked={this.state.sidebarDocked}
          open={this.state.sidebarOpen}
          styles={{
            sidebar: {
              background: "#e6f1ff",
              color: "#303C55",
              padding: "10px",
              top: "80px",
              transition: "transform .3s ease-out",
              willChange: "transform",
              overflowY: "auto",
              width: SIDEBAR_WIDTH,
            },
          }}
        />

        <Container
          style={{
            padding: 30,
            alignItems: "center",
            marginTop: 80,
            marginLeft: this.state.sidebarWidth,
          }}
        >
          <AboutSection />
          <ProjectPage />
        </Container>
      </div>
    );
  }
}

ReactDOM.render(<HomeScreen />, document.getElementById("root"));
