import React from "react";
import Card from "react-bootstrap/Card";
import { ListGroup, Container, Row, Col, Badge } from "react-bootstrap";
import "../index.css";
import "./Coaching.css";

const myOffer =
  "As your coach, I provide online programming, weekly+ checkins, lifting technique reviews, and nutrition information and strategies.";
const myProgramming =
  "My programs are individualized: each program is taylored to your goals and your training history. They're auto-regulated, so you can make the most out of both good and bad days, and they're data-driven, so they get better over time and you make undeniable progress.";
const myPhilosophy =
  "My job is to help you reach your goals. Those goals can be to competete in powerlifting, to get jacked, or simply to improve your quality of life and health. Regardless, together we find an entry point for you to get started, and then we build from there by being consistent. I want you to enjoy training and to feel enpowered and in control of your fitness.";
const offer = [
  "online programming",
  "weekly+ checkins",
  "lifting technique review",
  "nutrition information and strategies",
];
const philosophy = [
  "consistency",
  "self-efficacy",
  "find a way to train that you enjoy",
  "embrace the process",
];
const programming = [
  "individualized: each program is taylored to your goals and your training history",
  "auto-regulated: we all have good days and bad days––I want you to make the most of both",
  "data-driven: after every block, we look back to make sure what we're doing is working",
  "sustainable: evidence-based injury risk reduction and management so you continue to lift for the rest of your life",
];
const powerlifting = ["Specalize your squat, bench and deadlift", "Compete"];
const health = [
  "Build muscle",
  "Lose fat",
  "Meet (then exceed) exercise guidelines for health",
];

class Section extends React.Component {
  //props={header, items}
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div>
        <Container>
          <Row>
            <Col>
              <Row>
                <Badge
                  style={{
                    backgroundColor: "DarkSlateGray",
                    paddingLeft: "3vw",
                    paddingRight: "3vw",
                    fontSize: "24px",
                    color: "white",
                  }}
                >
                  {this.props.header}
                </Badge>
              </Row>
              {this.props.items.map((item, i) => (
                <Row>
                  <Badge className="label">-{item}</Badge>
                </Row>
              ))}
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default class Coaching extends React.Component {
  /* cards: 
  - I offer:
    - online programming
    - weekly+ checkins
    - lifitng technique review
    - nutrition information and effective diet strategies 
  - My philosophy is:
    - Consistency
    - Self-efficacy
    - Find a way to train that you enjoy 
    - Embrace the process
  - My programming is:
    - individualized: each program is taylored to your goals and your training history
    - auto-regulated: we all have good days and bad days––I want you to make the most of both 
    - data-driven: after every week and every block, we look back to make sure what we're doing is working
    - sustainable: evidence-based injury risk reduction and management so you continue to lift for the rest of your life
  - Powerlifting, img 
    - Maximize Squat, Bench, Deadlift 
  - Strength and Conditioning, img
    - Meet exercise guidelines for health
    - Improve your body composition
    - Build muscle, lose fat
    */

  render() {
    return (
      <div className="coaching">
        <Container>
          <Row>
            <Col>
              <Section header={"I offer"} items={offer} />
              <Section header={"My philosophy is"} items={philosophy} />
              <Section header={"My programs are"} items={programming} />
            </Col>
            <Col>
              <Section header={"Powerlifting"} items={powerlifting} />

              <Section header={"Health and Fitness"} items={health} />
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}
