import React from "react";
import "../index.css";
import Badge from "react-bootstrap/Badge";

const BTW = "I'm a nerdy Powerlifter.";
const ABOUT =
  "This is my training site. Here lives a public log of my training, as well as some training-related rambles. I hope you find something interesting--thanks for stopping by!";
const PRs = "Current Bests: Squat 500 | Bench 286 | Deadlift 600";

export default class Banner extends React.Component {
  render() {
    return (
      <div
        style={{
          zIndex: "99",
          alignItems: "center",
          justifyContent: "center",
          width: "auto",
          height: "auto",
          fontWeight: "500",
          textAlign: "center",
          color: "#FFFFFF",
          fontSize: "5vw",
          // display: "inline-block",
        }}
      >
        <p style={{ lineHeight: "1px" }}>{BTW}</p>
        <span style={{ fontSize: "3vw" }}>{PRs}</span>
        <p />
        <div
          style={{
            fontSize: "2.5vw",
            verticalAlign: "middle",
            overflow: "visible",
            width: "60vw",
            display: "inline-block",
            backgroundColor: "DarkSlateGray",
          }}
        >
          <span
            style={{
              margin: "1vw",
              display: "block",
              textAlign: "center",
            }}
          >
            {ABOUT}
          </span>
        </div>
      </div>
    );
  }
}
