        ðŸ‡ºðŸ‡¸
      </span>{" "}
    </Tooltip>
  );
}

export default class Banner extends React.Component {
  render() {
    return (
      <div
        style={{
          width: "auto",
          height: "auto",
          // fontWeight: "500",
          textAlign: "center",
          color: "#FFFFFF",
          fontSize: "5vw",
          // margin: "auto",
          padding: "auto",
          display: "block",
        }}
      >
        <OverlayTrigger
          placement="top"
          overlay={btwTooltip}
          delay={{ show: 100, hide: 1000 }}
        >
          <div className="overlay_text">{BTW}</div>
        </OverlayTrigger>

        <div>
          <OverlayTrigger
            placement="right"
            overlay={kgToLbsTooltip}
            delay={{ show: 100, hide: 1000 }}
          >
            <div className="overlay_text_small">{PRs}</div>
          </OverlayTrigger>
        </div>

        <div className="overlay_text">{ABOUT}</div>
      </div>
    );
  }
}
