export default class Banner extends React.Component {
  render() {
    return (
      <div
        style={{
          width: "auto",
          height: "auto",
          // fontWeight: "500",
          textAlign: "center",
          color: "#FFFFFF",
          fontSize: "5vw",
          // margin: "auto",
          padding: "auto",
          display: "block",
        }}
      >
        <OverlayTrigger
          placement="top"
          overlay={btwTooltip}
