}

export default class Banner extends React.Component {
  render() {
    return (
      <div
        style={{
          width: "auto",
          height: "auto",
          // fontWeight: "500",
          textAlign: "center",
          color: "#FFFFFF",
          fontSize: "5vw",
          // margin: "auto",
          padding: "auto",
          display: "block",
        }}
      >
        <OverlayTrigger
          placement="top"
          overlay={btwTooltip}
          delay={{ show: 100, hide: 1000 }}
        >
          <div className="overlay_text">{BTW}</div>
        </OverlayTrigger>

        <OverlayTrigger
          placement="right"
          overlay={kgToLbsTooltip}
          delay={{ show: 100, hide: 1000 }}
        >
          <div className="overlay_text_small">{PRs}</div>
        </OverlayTrigger>

        <div
          style={{
            fontSize: "2.5vw",
            borderRadius: "10px",
            margin: "3vw",
          }}
        >
          <span
            style={{
              padding: "1vw",
              backgroundColor: "DarkSlateGray",
            }}
          >
            {ABOUT}
          </span>
        </div>
      </div>
    );
  }
}
