import React from "react";
import { Col, Container, Row } from "react-bootstrap";
import { Chart } from "react-charts";
import "../index.css";
const db = { bw: [180, 181, 182, 181, 180] };

// array of 2-uple
function getBWData() {
  return db.bw.map((bw, i) => [bw, i]);
}

function BodyWeightChart() {
  const data = React.useMemo(
    () => [
      {
        label: "Weekly Running Average Body Weight",
        data: getBWData(),
      },
    ],
    []
  );
  const axes = React.useMemo(
    () => [
      { primary: true, type: "linear", position: "bottom" },
      { type: "linear", position: "left" },
    ],
    []
  );

  const lineChart = (
    <div
      style={{
        width: "400px",
        height: "300px",
      }}
    >
      <Chart data={data} axes={axes} />
    </div>
  );
}

class BodyWeightWidget extends React.Component {
  render() {
    return (
      <div>
        <Container>
          <Row>
            {/* Text */}
            <Col>
              <h3 className="header_smaller">Body Weight</h3>
              <p>Today:</p>
              <p>Weekly Running Average:</p>
              <hr />
              <p>Currently maintaining weight.</p>
            </Col>
            {/* Graph */}
            <Col>{/* <BodyWeightWidget /> */}</Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default class Log extends React.Component {
  constructor() {
    super();
    this.state = {};
  }

  componentDidMount() {
    this.setState({});
  }

  render() {
    return (
      <div
        style={{
          textAlign: "center",
          margin: "auto",
          padding: "30px",
        }}
      >
        <h1 className="header">The Dashboard</h1>
        <p className="header_smaller">
          This page is currently under development.
        </p>
        {/* Training Block Container */}
        <Container>
          <Row>
            {/* SBD deltas col */}
            <Col></Col>
            {/* Training Block summary col */}
            <Col></Col>
            {/* Next meet Col */}
            <Col></Col>
          </Row>
        </Container>
        {/* Training Week Container */}
        <Container>
          <Row>
            <Col></Col>
          </Row>
        </Container>

        {/* Body Weight Container */}
        <BodyWeightWidget />
      </div>
    );
  }
}
